select i.Instance_ID, i.Primary_ip4, ch.cloud_cluster, ch.ha_name, ch.insert_time from `Instance` i where  Date(ch.insert_time) = date_sub(curdate(), interval 2 day) inner join Cloud_HA ch on ch.compute_ip = i.Primary_ip4 union all select y.Instance_ID, y.Primary_ip4, ch.cloud_cluster, ch.ha_name, ch.insert_time from Cloud_HA ch where  Date(ch.insert_time) = date_sub(curdate(), interval 2 day) inner join (select Instance_ID, Primary_ip4 from `Instance` inner join (select Parent_instance from `Instance` where Device_ID is NULL and Parent_instance !=1) as x 
on Instance_ID = x.Parent_instance) as y 	/*la dieu kien cua inner join o dong 7*/
on y.Primary_ip4 = ch.compute_ip 	/*la dieu kien cua cau select o dong 5*/
